    return (
        <li className="expense-item">
            <ExpenseDate date={props.date}/>
            {/* <div>{props.date}</div> */}
            <div className="expense-item__price">{props.amount}</div>
            <h2 className="expense-item__description">{props.title}</h2>
        </li>

    )


}
