import { useState } from "react"
import { ExpenseForm } from "./ExpenseForm/ExpenseForm"

export const NewExpense = () => {

    const {showForm,setShowForm} = useState(false);

    const setValue = (val) => {
        setShowForm(val)
    }

    const content = <button onClick={() => {setValue(!showForm)}}>Add New Expense</button>


    if (showForm){
        const content = <ExpenseForm onCancelClick={(data , event) => {console.log('g' + data)}}/>

    }

    return (
        <div>

            {content}
        </div>

    )

}
